function setdis(obj)
{
    if(obj.style.display=='none')
    {
        obj.style.display='block';
        return 1;
    }
    else
    {
        obj.style.display='none';
        return 0;
    }
}
function getpwd(ssid)
{
    var flag=cpno() && cvcd();
    if (flag) {
		sbfm(ssid);
	}
}
function sbfm(ssid) 
{
    url1="/buyEcard";
    params = "phoneNo=" + $$("username1").value + "&validate=" + $$("validate3").value + "&serviceId=00000&vcodeindex=3&ssid="+ssid;
	ajaxpost(url1,params);
}
var xmlHttp;
function createXMLHttpRequest() {
    if (window.ActiveXObject) {
        xmlHttp = new ActiveXObject("Microsoft.XMLHTTP");
    }
    else if (window.XMLHttpRequest) {
        xmlHttp = new XMLHttpRequest();
    }
}
var okFunc = function () {
    if (xmlHttp.readyState == 4) {
        if (xmlHttp.status == 200) {
            ri('validate3','3');
            $$("validate3").value="";         
            alert(xmlHttp.responseText);
        }
    }
}
function ajaxpost(url,parameter) {   
    createXMLHttpRequest();
    if (!xmlHttp) {
        return alert('create failed');
    }
    xmlHttp.onreadystatechange = okFunc;
    xmlHttp.open("POST", url, true);
    xmlHttp.setRequestHeader("Content-type", "application/x-www-form-urlencoded");
    xmlHttp.send(parameter);
    
}
function cvcd() {
	var code = $$("validate3").value;
	if (code == ""){
	    alert("请输入验证码");
	    return false;
	}else {
		return true;
	}
}
function cpno() {
    var regExp = /^1[1-9][\d]{9}$/;
	var mobileNumber = $$("username1");
	if (mobileNumber == null || mobileNumber.value == "") {
		alert("请输入手机号码");
		mobileNumber.select();
		return false;
	}
	if (!mobileNumber.value.match(regExp)) {
		alert("系统目前仅支持移动手机用户。非移动手机用户如需使用WLAN业务，请在线购买时长卡后，使用时长卡帐号密码进行登录使用。");
		mobileNumber.select();
		return false;
	} else {
		return true;
	}
}
